import React, { useState, useEffect } from 'react';
import { 
  Typography, 
  Grid, 
  Box, 
  CircularProgress,
  Alert
} from '@mui/material';
import { 
  AssignmentOutlined, 
  ShoppingCartOutlined, 
  PersonOutlineOutlined, 
  InventoryOutlined 
} from '@mui/icons-material';
import DashboardCard, { OrdersChart } from '../components/DashboardComponents';
import { dashboardService } from '../services/api';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [summaryData, setSummaryData] = useState({
    totalOrders: 0,
    totalClients: 0,
    totalProducts: 0,
    pendingOrders: 0
  });
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [
      {
        label: 'Pedidos',
        data: [],
        backgroundColor: 'rgba(25, 118, 210, 0.6)',
      }
    ]
  });

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // Carrega os dados do resumo
        const summaryRes = await dashboardService.getSummary();
        setSummaryData(summaryRes.data);
        
        // Carrega os dados do gráfico de pedidos por dia
        const ordersPerDayRes = await dashboardService.getOrdersPerDay();
        
        // Preparar dados para o gráfico
        const labels = ordersPerDayRes.data.map(item => item.date);
        const values = ordersPerDayRes.data.map(item => item.count);
        
        setChartData({
          labels,
          datasets: [
            {
              label: 'Pedidos',
              data: values,
              backgroundColor: 'rgba(25, 118, 210, 0.6)',
            }
          ]
        });
        
      } catch (err) {
        console.error('Erro ao carregar dados do dashboard:', err);
        setError('Não foi possível carregar os dados do dashboard. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchDashboardData();
  }, []);

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>
      
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}
      
      <Grid container spacing={3}>
        {/* Cards de resumo */}
        <Grid item xs={12} sm={6} md={3}>
          <DashboardCard 
            title="Total de Pedidos" 
            value={summaryData.totalOrders} 
            loading={loading}
            icon={<ShoppingCartOutlined />}
            color="primary"
          />
        </Grid>
        
        <Grid item xs={12} sm={6} md={3}>
          <DashboardCard 
            title="Pedidos Pendentes" 
            value={summaryData.pendingOrders} 
            loading={loading}
            icon={<AssignmentOutlined />}
            color="warning"
          />
        </Grid>
        
        <Grid item xs={12} sm={6} md={3}>
          <DashboardCard 
            title="Clientes Cadastrados" 
            value={summaryData.totalClients} 
            loading={loading}
            icon={<PersonOutlineOutlined />}
            color="success"
          />
        </Grid>
        
        <Grid item xs={12} sm={6} md={3}>
          <DashboardCard 
            title="Produtos Cadastrados" 
            value={summaryData.totalProducts} 
            loading={loading}
            icon={<InventoryOutlined />}
            color="info"
          />
        </Grid>
        
        {/* Gráfico de pedidos por dia */}
        <Grid item xs={12} md={8}>
          <OrdersChart data={chartData} loading={loading} />
        </Grid>
        
        {/* Widget de pedidos recentes ou produtos mais vendidos pode ser adicionado aqui */}
        <Grid item xs={12} md={4}>
          {/* Componente para produtos mais vendidos pode ser adicionado aqui */}
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
