import axios from 'axios';

// Configuração do Axios
const api = axios.create({
  baseURL: process.env.NODE_ENV === 'production' ? '' : 'http://localhost:3001',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Services para o Dashboard
export const dashboardService = {
  getSummary: () => api.get('/dashboard/summary'),
  getOrdersPerDay: () => api.get('/dashboard/orders-per-day'),
  getTopProducts: () => api.get('/dashboard/top-products')
};

// Services para Clientes
export const clientService = {
  getAll: () => api.get('/clients'),
  getById: (id) => api.get(`/clients/${id}`),
  create: (data) => api.post('/clients', data),
  update: (id, data) => api.put(`/clients/${id}`, data),
  delete: (id) => api.delete(`/clients/${id}`)
};

// Services para Produtos
export const productService = {
  getAll: () => api.get('/products'),
  getById: (id) => api.get(`/products/${id}`),
  create: (data) => api.post('/products', data),
  update: (id, data) => api.put(`/products/${id}`, data),
  delete: (id) => api.delete(`/products/${id}`)
};

// Services para Pedidos
export const orderService = {
  getAll: () => api.get('/orders'),
  getById: (id) => api.get(`/orders/${id}`),
  create: (data) => api.post('/orders', data),
  update: (id, data) => api.put(`/orders/${id}`, data),
  delete: (id) => api.delete(`/orders/${id}`),
  updateStatus: (id, status) => api.patch(`/orders/${id}/status`, { status }),
  generatePDF: (id) => api.get(`/orders/${id}/pdf`, { responseType: 'blob' })
};

export default {
  dashboardService,
  clientService,
  productService,
  orderService
};
